// array to hold "random" pixels
var ranNums = [];

// create a non-overlapping array of random numbers 0-8
function rerand() {
    var nums = [0,1,2,3,4,5,6,7,8];
    var i = nums.length;
    var j;
    ranNums = [];
    while (i--) {
        j = Math.floor(Math.random() * (i+1));
        ranNums.push(nums[j]);
        nums.splice(j,1);
    }
}

// Get the hours and minutes and split into tens and units
var d = new Date();
var s = d.getSeconds();
var su = s%4;
if (su === 0) {
    var h = d.getHours();
    var m = d.getMinutes();
    var hu = h%10;
    h = parseInt(h/10);
    var mu = m%10;
    m = parseInt(m/10);

    // Do the tens of hours (red)
    rerand();
    node.send({payload:"1-3,1-3,0,0,0"});
    for (var i=0; i<h; i++) {
        node.send({payload:(1+ranNums[i]%3)+","+(1+parseInt(ranNums[i]/3))+",255,0,0"});
    }

    // Do the units of hours (green)
    rerand();
    node.send({payload:"4-6,1-3,0,0,0"});
    for (var i=0; i<hu; i++) {
        node.send({payload:(4+ranNums[i]%3)+","+(1+parseInt(ranNums[i]/3))+",0,255,0"});
    }

    // Do the tens of minutes (yellow)
    rerand();
    node.send({payload:"1-3,4-6,0,0,0"});
    for (var i=0; i<m; i++) {
        node.send({payload:(1+ranNums[i]%3)+","+(4+parseInt(ranNums[i]/3))+",255,255,0"});
    }

    // Do the unit of minutes (blue)
    rerand();
    node.send({payload:"4-6,4-6,0,0,0"});
    for (var i=0; i<mu; i++) {
        node.send({payload:(4+ranNums[i]%3)+","+(4+parseInt(ranNums[i]/3))+",0,0,255"});
    }

}
node.send({payload:"0,0,"+(su===0?"purple":"off")+",7,0,"+(su===1?"purple":"off")+",7,7,"+(su===2?"purple":"off")+",0,7,"+(su===3?"purple":"off")})


// nothing left to do
return null;
